// src/services/playlistService.js

const playlistService = (() => {
    let playlist = [
      {
        id: 1,
        title: "fukdafrailshi",
        artist: "Chico Mcgee",
        url: "public/tracks/fukdafrailshii .wav", // Local file path
        artwork: "public/artwork/artwork.PNG",
      },
      {
        id: 2,
        title: "Benjaminz",
        artist: "Chico Mcgee",
        url: "public/artwork/artwork.PNG",
        artwork: "/public/artwork/IMG_5336.jpeg",
      },
      {
        id: 3,
        title: "Neon Lights",
        artist: "Something Vague",
        url: "public/tracks/neon-lights.mp3",
        artwork: "/public/artwork/neon-lights-cover.jpg",
      },
      {
        id: 4,
        title: "Late Night Drive",
        artist: "Something Vague",
        url: "public/tracks/late-night-drive.mp3",
        artwork: "/public/artwork/late-night-cover.jpg",
      },
      {
        id: 5,
        title: "Urban Dreams",
        artist: "Chico Mcgee",
        url: "public/tracks/urban-dreams.mp3",
        artwork: "/public/artwork/urban-dreams.jpg",
      },
      {
        id: 6,
        title: "Midnight Blues",
        artist: "Something Vague",
        url: "public/tracks/midnight-blues.mp3",
        artwork: "/public/artwork/midnight-blues.jpg",
      },
    ];

    return {
      getPlaylist: () => playlist,

      addTrack: (track) => {
        track.id = playlist.length ? playlist[playlist.length - 1].id + 1 : 1;
        playlist.push(track);
      },

      removeTrack: (id) => {
        playlist = playlist.filter((track) => track.id !== id);
      },

      getTrackById: (id) => {
        return playlist.find((track) => track.id === id);
      },
    };
  })();

  export default playlistService;
