import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import '../../styles/auth.css';

function Login({ setUser }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  // Check if we were redirected from another page
  const from = location.state?.from?.pathname || '/';

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5001/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Login failed');
      }

      // Store the token in localStorage
      localStorage.setItem('token', data.token);

      // Set the user in the app state
      setUser({
        ...data.user,
        isAdmin: data.user.role === 'ADMIN'
      });

      // If admin, redirect to blog admin, else go to requested page or home
      if (data.user.role === 'ADMIN') {
        navigate('/blog-admin');
      } else {
        navigate(from);
      }
    } catch (err) {
      setError('Login failed: ' + (err.message || 'Unknown error'));
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={handleLogin}>
        <h2>Login</h2>

        {error && <p className="error-message">{error}</p>}

        <input 
          type="email" 
          placeholder="Email" 
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required 
        />
        <button type="submit">Login</button>
        <p>Don't have an account? <a href="/register">Register</a></p>

        {/* Add admin login hint */}
        <div className="login-hint">
          {/* <p>For admin access, use an email ending with @admin.com</p> */}
        </div>
      </form>
    </div>
  );
}

export default Login;
