import React, { useState, useRef, useEffect } from "react";
import playlistService from "../services/playlistService";
import '../styles/player.css'; // Ensure path is correct

// Keep font face definition if needed for other elements, or remove if unused
const fontFaceStyles = `
  @font-face {
    font-family: 'Daydream';
    src: url('/fonts/Daydream.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
  }
`;

function Player() {
    const [playlist, setPlaylist] = useState([]);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const audioRef = useRef(null);
    const originalBodyStyleRef = useRef(null);

    // --- Hooks remain largely the same ---
    // Load playlist
    useEffect(() => {
        const tracks = playlistService.getPlaylist();
        setPlaylist(tracks);
        setIsLoading(false);
    }, []);

    // Global Background Effect
    useEffect(() => {
        if (playlist.length === 0 || isLoading) return;
        const currentTrackInfo = playlist[currentTrackIndex];
        const artworkUrl = process.env.PUBLIC_URL + currentTrackInfo.artwork.replace('public/', '');
        if (originalBodyStyleRef.current === null) { originalBodyStyleRef.current = { /* store */ }; }
        document.body.style.backgroundImage = `url(${artworkUrl})`;
        document.body.style.backgroundSize = 'cover';
        document.body.style.backgroundPosition = 'center center';
        document.body.style.backgroundAttachment = 'fixed';
        document.body.style.minHeight = '100vh';
        document.body.style.transition = 'background-image 1s ease-in-out';
        return () => { if (originalBodyStyleRef.current) { /* restore */ } };
    }, [currentTrackIndex, playlist, isLoading]);

    // Track loading and playback
    useEffect(() => {
        if (playlist.length === 0 || isLoading) return;
        const audio = audioRef.current;
        if (!audio) return;
        const trackUrl = process.env.PUBLIC_URL + playlist[currentTrackIndex].url.replace('public/', '');
        audio.src = trackUrl; audio.load();
        const attemptAutoplay = () => { /* ... */ };
        const handleEnded = () => { setCurrentTrackIndex(prev => (prev + 1) % playlist.length); };
        audio.addEventListener('ended', handleEnded);
        const canPlayHandler = () => { attemptAutoplay(); audio.removeEventListener('canplay', canPlayHandler); }
        audio.addEventListener('canplay', canPlayHandler);
        return () => { audio.removeEventListener('ended', handleEnded); audio.removeEventListener('canplay', canPlayHandler); };
    }, [currentTrackIndex, playlist, isLoading]);

    // Audio State Event Listeners
    useEffect(() => {
        const audio = audioRef.current;
        if (!audio) return;
        const handlePlay = () => setIsPlaying(true); const handlePause = () => setIsPlaying(false); const handleError = (e) => console.error("Audio error:", e);
        audio.addEventListener('play', handlePlay); audio.addEventListener('pause', handlePause); audio.addEventListener('error', handleError);
        return () => { audio.removeEventListener('play', handlePlay); audio.removeEventListener('pause', handlePause); audio.removeEventListener('error', handleError); };
    }, [playlist.length]);

    // Toggle Play Function
    const togglePlay = () => {
        const audio = audioRef.current; if (!audio) return;
        if (isPlaying) { audio.pause(); }
        else { audio.play().catch(error => { console.error("Playback error:", error); setIsPlaying(false); }); }
    };

    // --- Loading State ---
    if (isLoading || playlist.length === 0) {
        return <div className="loading-container"><p>Loading...</p></div>; // Simplified loading
    }

    // --- Render Minimal Player Bar ---
    const currentTrackInfo = playlist[currentTrackIndex];

    return (
        <>
            {/* Inject font styles if needed, otherwise ensure loaded globally */}
            {/* <style dangerouslySetInnerHTML={{ __html: fontFaceStyles }} /> */}

            {/* The audio element remains */}
            <audio ref={audioRef} preload="metadata" />

            {/* --- The Player Bar --- */}
            <div className="player-bar">

                {/* Section 1: Live Now Indicator */}
                <div className="live-section">
                    <span>LIVE NOW</span>
                    <span className="blinking-dot"></span>
                </div>

                {/* Section 2: Play/Pause Button */}
                <button onClick={togglePlay} className="play-pause-icon-button" aria-label={isPlaying ? "Pause" : "Play"}>
                    {isPlaying ? '⏸️' : '▶️'} {/* Simple text icons */}
                </button>

                {/* Section 3: Track Info */}
                <div className="track-display">
                    {/* You can combine title and artist or show only one */}
                    <span className="track-title">{currentTrackInfo.title}</span>
                    {currentTrackInfo.artist && <span className="track-artist"> - {currentTrackInfo.artist}</span>}
                </div>

                {/* Section 4: Location (Hardcoded example) */}
                <div className="location-display">
                    LONDON
                </div>

            </div>

            {/* The main content area is now just the body background */}
            {/* If you needed content BELOW the bar, you'd add padding-top to the body or a main content wrapper */}

        </>
    );
}

export default Player;
