import React, { useState } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom'; // Import useLocation here
import Navbar from './components/Navbar';
import Home from './pages/Home';
// Player component is rendered below Routes, so import is still needed
import Player from './components/Player';
import Blog from './pages/Blog';
import Submit from './pages/Submit';
import Help from './pages/Help';
import Login from './components/AuthSystem/Login';
import Register from './components/AuthSystem/Register';
import './App.css'; // Import App specific CSS

// A helper component to access location within App
function AppContent() {
  const location = useLocation();
  const isHomePage = location.pathname === '/';
  const [user, setUser] = useState(null); // User state remains here

  // Define the height of the bottom player bar + a little extra space
  const bottomPlayerBarHeight = '80px'; // Adjust to match your CSS + desired padding

  return (
    // Apply padding dynamically to a wrapper around Routes/content area
    // Exclude Navbar and Player from this padding adjustment area
    <div className="app-container">
      <Navbar user={user} setUser={setUser} />

      {/* This div gets the padding */}
      <div
          className="main-content-area"
          style={{ paddingBottom: !isHomePage ? bottomPlayerBarHeight : '0' }}
      >
          <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/blog" element={<Blog />} />
              <Route path="/submit" element={<Submit />} />
              <Route path="/help" element={<Help />} />
              <Route path="/login" element={<Login setUser={setUser} />} />
              <Route path="/register" element={<Register setUser={setUser} />} />
          </Routes>
      </div>

      {/* Player is outside the padded area and Routes */}
      <Player />
    </div>
  );
}


// Main App component now just sets up the Router
function App() {
   // Wrap with Router if not already done in index.js
  // If BrowserRouter is in index.js, remove it from here.
  // Assuming BrowserRouter is needed here:
  // return (
  //   <Router>
  //     <AppContent />
  //   </Router>
  // );

  // If Router is in index.js:
   return <AppContent />;

}


export default App;
