import React, { useState, useRef, useEffect } from "react";
import playlistService from "../services/playlistService"; // Import playlist service

function Player() {
  const [playlist, setPlaylist] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  // Load playlist from service
  useEffect(() => {
    const tracks = playlistService.getPlaylist();
    setPlaylist(tracks);

    if (tracks.length === 0) {
      console.error("No tracks found in playlist.");
    }
  }, []);

  useEffect(() => {
    if (isPlaying && audioRef.current) {
      audioRef.current.play().catch((error) => {
        console.error("Error playing audio:", error);
      });
    } else if (audioRef.current) {
      audioRef.current.pause();
    }
  }, [currentTrack, isPlaying]);

  const togglePlay = () => {
    if (playlist.length === 0) return;
    setIsPlaying(!isPlaying);
  };

  const nextTrack = () => {
    if (playlist.length === 0) return;
    setCurrentTrack((prev) => (prev + 1) % playlist.length);
  };

  // Prevent errors if playlist is empty
  if (playlist.length === 0) {
    return <p>Loading playlist or no tracks available...</p>;
  }

  const currentTrackInfo = playlist[currentTrack];

  return (
    <div
      className="player-container"
      style={{
        backgroundImage: `url(${currentTrackInfo.artwork})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <audio ref={audioRef} onEnded={nextTrack} controls>
        <source src={currentTrackInfo.url} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>

      <div className="track-info">
        <h2>{currentTrackInfo.title}</h2>
        <p>{currentTrackInfo.artist}</p>
      </div>

      <div className="player-controls">
        <button onClick={togglePlay}>
          {isPlaying ? "Pause" : "Play"}
        </button>
        <button onClick={nextTrack}>Next</button>
      </div>
    </div>
  );
}

export default Player;
