import React from 'react';
import { Link } from 'react-router-dom';

// Keep the font face styles
const fontFaceStyles = `
  @font-face {
    font-family: 'Daydream';
    src: url('/fonts/Daydream.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
  }
`;

function Home() {
  return (
    <>
      <style dangerouslySetInnerHTML={{ __html: fontFaceStyles }} />

      {/* We will modify styles.container below */}
      <div style={styles.container}>
        {/* Add an overlay div for text readability */}
        <div style={styles.overlay}>
            <div style={styles.hero}>
              <h1 style={styles.title}>Something Vague</h1>
              <p style={styles.subtitle}>Hear something new</p>

              {/* Optional: Add a link back to the player */}
              <div style={styles.ctaContainer}>
                 <Link to="/player" style={styles.primaryButton}>
                   Tune In
                 </Link>
               </div>

            </div>
        </div>
      </div>
    </>
  );
}

const styles = {
  container: {
    // REMOVE backgroundColor: '#ffffff',
    minHeight: '100vh', // Make container fill viewport height
    padding: '0', // Remove padding or adjust as needed if overlay handles it
    display: 'flex', // Keep flex for centering overlay/hero
    flexDirection: 'column',
    justifyContent: 'center', // Center content vertically
    alignItems: 'center', // Center content horizontally
    position: 'relative', // Needed if using absolute positioning for overlay
    width: '100%', // Ensure it spans width
  },
  overlay: { // Added overlay style
      position: 'relative', // Or absolute if container is relative
      width: '100%',
      minHeight: '100vh', // Ensure overlay also covers height
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '2rem', // Add padding here instead of container
      boxSizing: 'border-box',
      backgroundColor: 'rgba(0, 0, 0, 0.4)', // Semi-transparent black overlay
      color: '#ffffff', // Set default text color for overlay content
  },
  hero: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    maxWidth: '600px',
    margin: '0 auto', // Centers hero within overlay/container
    zIndex: 1, // Ensure hero content is above overlay bg if needed
    color: '#ffffff', // Ensure text color contrasts with overlay
  },
  title: {
    fontFamily: "'Daydream', sans-serif",
    fontSize: '3.5rem', // Maybe make slightly larger on dark bg
    fontWeight: 'normal',
    color: '#ffffff', // White text
    marginBottom: '0.5rem',
    letterSpacing: '1px',
    textShadow: '1px 1px 3px rgba(0,0,0,0.5)', // Optional shadow for readability
  },
  subtitle: {
    fontFamily: "'Daydream', sans-serif",
    fontSize: '1.3rem', // Maybe make slightly larger
    color: '#dddddd', // Lighter grey or white
    marginBottom: '3rem',
    lineHeight: '1.6',
    fontWeight: '300', // Keep light weight if font supports it
     textShadow: '1px 1px 3px rgba(0,0,0,0.5)', // Optional shadow
  },
  ctaContainer: {
    margin: '2rem 0 3rem',
  },
  primaryButton: {
    // Style button for dark background
    backgroundColor: 'rgba(255, 255, 255, 0.8)', // White-ish button
    color: '#111111', // Dark text
    padding: '1rem 3rem',
    borderRadius: '50px', // Make it round like player button?
    textDecoration: 'none',
    fontWeight: 'bold', // Bolder
    letterSpacing: '1px',
    fontSize: '1rem',
    transition: 'all 0.2s ease',
    display: 'inline-block',
    border: 'none', // Remove border if any
     boxShadow: '0px 2px 5px rgba(0,0,0,0.3)', // Optional shadow
  },
   primaryButtonHover: { // You'd need JS to handle hover on inline styles or use CSS Modules/Styled Comp
     backgroundColor: 'rgba(255, 255, 255, 1)',
     transform: 'scale(1.05)',
   },
  // Remove styles for nowPlaying section as it was commented out
  // nowPlaying: { ... },
  // nowPlayingText: { ... },
  // currentTrack: { ... },
  // genre: { ... },
};

export default Home;
