import React, { useState, useRef, useEffect } from "react";
import playlistService from "../services/playlistService";
import '../styles/player.css'; // Make sure the path is correct

// Define the font face styles directly here OR ensure it's in Player.css
// Option 1: Inject style tag (if not putting @font-face in Player.css)
const fontFaceStyles = `
  @font-face {
    font-family: 'Daydream';
    src: url('/fonts/Daydream.ttf') format('truetype');
    font-weight: normal;
    font-style: normal;
  }
`;

function Player() {
    const [playlist, setPlaylist] = useState([]);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const audioRef = useRef(null);
    const originalBodyStyleRef = useRef(null);

    // Load playlist
    useEffect(() => {
        const tracks = playlistService.getPlaylist();
        setPlaylist(tracks);
        setIsLoading(false);
    }, []);

    // --- Global Background Effect (Remains the same) ---
    useEffect(() => {
        if (playlist.length === 0 || isLoading) return;

        const currentTrackInfo = playlist[currentTrackIndex];
        const artworkUrl = process.env.PUBLIC_URL + currentTrackInfo.artwork.replace('public/', '');

        if (originalBodyStyleRef.current === null) {
             originalBodyStyleRef.current = { /* ... store original styles ... */ };
        }

        document.body.style.backgroundImage = `url(${artworkUrl})`;
        document.body.style.backgroundSize = 'cover';
        document.body.style.backgroundPosition = 'center center';
        document.body.style.backgroundAttachment = 'fixed';
        document.body.style.minHeight = '100vh';
        document.body.style.transition = 'background-image 1s ease-in-out';

        return () => {
             if (originalBodyStyleRef.current) { /* ... restore original styles ... */ }
        };
    }, [currentTrackIndex, playlist, isLoading]);

    // Handle track loading and playback (Remains the same)
    useEffect(() => {
        if (playlist.length === 0 || isLoading) return;
        const audio = audioRef.current;
        if (!audio) return;
        // ... (rest of the track loading/event logic) ...
         const trackUrl = process.env.PUBLIC_URL + playlist[currentTrackIndex].url.replace('public/', '');
        audio.src = trackUrl;
        audio.load();

        const attemptAutoplay = () => { /* ... */ };
        const handleEnded = () => { setCurrentTrackIndex(prev => (prev + 1) % playlist.length); };

        audio.addEventListener('ended', handleEnded);
        const canPlayHandler = () => { attemptAutoplay(); audio.removeEventListener('canplay', canPlayHandler); }
        audio.addEventListener('canplay', canPlayHandler);

        return () => { audio.removeEventListener('ended', handleEnded); audio.removeEventListener('canplay', canPlayHandler); };
    }, [currentTrackIndex, playlist, isLoading]);


    // Event Listeners for Audio State (Remains the same)
    useEffect(() => {
        const audio = audioRef.current;
        if (!audio) return;
        // ... (play, pause, error listeners) ...
        const handlePlay = () => setIsPlaying(true);
        const handlePause = () => setIsPlaying(false);
        const handleError = (e) => { console.error("Audio error:", e); };
        audio.addEventListener('play', handlePlay);
        audio.addEventListener('pause', handlePause);
        audio.addEventListener('error', handleError);
        return () => { /* ... remove listeners ... */ };
    }, [playlist.length]);


    // Toggle Play Function (Remains the same)
    const togglePlay = () => {
        const audio = audioRef.current;
        if (!audio) return;
        if (isPlaying) { audio.pause(); }
        else { audio.play().catch(error => { console.error("Playback error on toggle:", error); setIsPlaying(false); }); }
    };

    // --- Loading State ---
    if (isLoading || playlist.length === 0) {
        return <div className="loading-container"><p>Loading player...</p></div>;
    }

    // --- Render Player with Integrated Header ---
    const currentTrackInfo = playlist[currentTrackIndex]; // Get current track info

    return (
        <>
            {/* Option 1: Inject style tag here if @font-face is not in CSS file */}
            <style dangerouslySetInnerHTML={{ __html: fontFaceStyles }} />

            <div className="player-container"> {/* This container is now transparent */}
                <audio ref={audioRef} preload="metadata" />

                {/* --- Integrated Header Section --- */}
                <div className="player-header">
                    <h1>Something Vague</h1>
                    <p>Hear something new</p>
                </div>
                {/* --- End of Header Section --- */}


                {/* --- Track Info Box (Semi-Transparent Background) --- */}
                <div className="track-info">
                    <div className="live-header">
                        <span className="blinking-dot"></span>
                        <h2>Live Now</h2>
                    </div>
                    <h3>{currentTrackInfo.title}</h3>
                    <p>{currentTrackInfo.artist}</p>
                </div>


                {/* --- Player Controls Box (Semi-Transparent Background) --- */}
                <div className="player-controls">
                    <button
                        onClick={togglePlay}
                        style={{ backgroundColor: isPlaying ? "#ff5555" : "#55ff55" }}
                        className="play-pause-button"
                    >
                        {isPlaying ? "PAUSE" : "PLAY"}
                    </button>
                </div>

            </div>
        </>
    );
}

export default Player;
