// src/components/SubmitPostForm.js
import React, { useState, useCallback } from 'react';
import axios from 'axios'; // For API calls
import { useDropzone } from 'react-dropzone';
import ReactQuill from 'react-quill'; // Rich Text Editor
import 'react-quill/dist/quill.snow.css'; // Default theme for Quill
import '.'; // Create this CSS file

// --- MOCK API FUNCTIONS ---
// ** REPLACE these with your actual API calls **
async function uploadFile(file) {
  console.log('Uploading file:', file.name);
  // Simulate upload delay
  await new Promise(resolve => setTimeout(resolve, 1500));

  // Create FormData to send the file
  const formData = new FormData();
  formData.append('file', file); // 'file' should match your backend's expected field name

  try {
    // ** REPLACE WITH YOUR ACTUAL UPLOAD ENDPOINT **
    // const response = await axios.post('/api/upload', formData, {
    //   headers: {
    //     'Content-Type': 'multipart/form-data',
    //     // Include Authorization header if needed
    //     // 'Authorization': `Bearer ${yourAuthToken}`
    //   }
    // });
    // return response.data; // Assuming backend returns { url: '...' } or { fileId: '...' }

    // --- Mock Response ---
    const mockUrl = `/uploads/mock-${Date.now()}-${file.name.replace(/\s+/g, '_')}`;
    console.log('Mock upload success:', mockUrl);
    return { url: mockUrl, filename: file.name }; // Return URL and original filename
    // --- End Mock Response ---

  } catch (error) {
    console.error("File upload failed:", error);
    throw error; // Re-throw error to be caught in handleSubmit
  }
}

async function createPost(postData) {
   console.log('Creating post with data:', postData);
   // Simulate API delay
   await new Promise(resolve => setTimeout(resolve, 1000));

   try {
     // ** REPLACE WITH YOUR ACTUAL POST CREATION ENDPOINT **
     // const response = await axios.post('/api/posts', postData, {
     //   headers: {
     //     // Include Authorization header if needed
     //      // 'Authorization': `Bearer ${yourAuthToken}`
     //   }
     // });
     // return response.data; // Assuming backend returns the created post object

     // --- Mock Response ---
      const mockCreatedPost = { ...postData, id: Date.now(), createdAt: new Date().toISOString() };
      console.log('Mock post creation success:', mockCreatedPost);
      return mockCreatedPost;
     // --- End Mock Response ---

   } catch (error) {
      console.error("Post creation failed:", error);
      throw error; // Re-throw error
   }
}
// --- END MOCK API FUNCTIONS ---


// Quill Modules configuration (optional, customize as needed)
const quillModules = {
  toolbar: [
    [{ 'header': [1, 2, 3, false] }],
    ['bold', 'italic', 'underline', 'strike', 'blockquote'],
    [{'list': 'ordered'}, {'list': 'bullet'}, {'indent': '-1'}, {'indent': '+1'}],
    ['link' /*, 'image' */], // Add image button if you want direct image embeds in text
    ['clean']
  ],
};

function SubmitPostForm() {
  const [postType, setPostType] = useState('text'); // Default type
  const [title, setTitle] = useState('');
  const [textContent, setTextContent] = useState('');
  const [files, setFiles] = useState([]); // Store file objects for upload
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  // react-dropzone setup
  const onDrop = useCallback(acceptedFiles => {
    console.log('Accepted files:', acceptedFiles);
    // Handle based on post type (allow multiple for slideshow, single otherwise?)
    const maxFiles = postType === 'slideshow' ? 10 : 1; // Example limit
    const newFiles = acceptedFiles.slice(0, maxFiles);

    // Basic validation (can be more sophisticated)
    const allowedTypes = {
        image_text: ['image/jpeg', 'image/png', 'image/gif', 'image/webp'],
        image_audio: ['image/jpeg', 'image/png', 'image/gif', 'image/webp', 'audio/mpeg', 'audio/ogg', 'audio/wav'],
        video: ['video/mp4', 'video/webm', 'video/ogg'],
        slideshow: ['image/jpeg', 'image/png', 'image/gif', 'image/webp'],
    };

    const currentAllowed = allowedTypes[postType] || [];
    const validFiles = newFiles.filter(file => currentAllowed.includes(file.type));

    if(validFiles.length !== newFiles.length){
        setError(`Invalid file type(s) selected for ${postType} post.`);
    } else {
        setError(null); // Clear error if valid
    }


    // For simplicity, let's replace files for single-file types, append for slideshow
    if (postType === 'slideshow') {
         setFiles(prev => [...prev, ...validFiles].slice(0, maxFiles)); // Append and limit
    } else if (validFiles.length > 0) {
        setFiles(validFiles); // Replace
    } else if (newFiles.length > 0 && validFiles.length === 0) {
         // Keep showing the error set above
    } else {
        setFiles([]); // Clear if no valid files dropped
    }

  }, [postType]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: { // Define accepted MIME types generally, filter further in onDrop
      'image/*': ['.jpeg', '.png', '.gif', '.webp'],
      'audio/*': ['.mp3', '.mpeg', '.ogg', '.wav'],
      'video/*': ['.mp4', '.webm', '.ogg']
    },
    multiple: postType === 'slideshow' // Allow multiple only for slideshows
  });

  const handleTypeChange = (type) => {
    setPostType(type);
    setFiles([]); // Clear files when changing type
    setTextContent('');
    setTitle('');
    setError(null);
    setSuccessMessage('');
  };

  const removeFile = (index) => {
    setFiles(prev => prev.filter((_, i) => i !== index));
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsSubmitting(true);
    setError(null);
    setSuccessMessage('');

    // Basic Validation
    if (!title && postType !== 'text') { // Title might be optional for text posts?
      setError('Title is required for this post type.');
      setIsSubmitting(false);
      return;
    }
    if (postType === 'text' && !textContent) {
       setError('Text content is required for a text post.');
       setIsSubmitting(false);
       return;
    }
     if ((postType === 'image_text' || postType === 'image_audio' || postType === 'video' || postType === 'slideshow') && files.length === 0) {
       setError('Please select or drop a file for this post type.');
       setIsSubmitting(false);
       return;
     }
     if(postType === 'image_audio' && files.length < 2 && files.filter(f => f.type.startsWith('image/')).length === 0 && files.filter(f => f.type.startsWith('audio/')).length === 0 ) {
        setError('Please provide both an image and an audio file for Image + Audio post.');
        setIsSubmitting(false);
        return;
     }


    try {
      // 1. Upload files (if any)
      const uploadedFileData = [];
      if (files.length > 0) {
          // Use Promise.all to upload files concurrently (or sequentially if needed)
          const uploadPromises = files.map(file => uploadFile(file));
          const results = await Promise.all(uploadPromises);
          uploadedFileData.push(...results); // results should be array of { url: '...', filename: '...' }
          console.log("All files uploaded:", uploadedFileData);
      }

      // 2. Prepare post data object for the backend
      const postData = {
        title,
        type: postType,
        text: textContent, // Send even if empty, backend can decide
        // Add file URLs/IDs based on type and uploaded data
        imageUrl: null,
        videoUrl: null,
        audioUrl: null,
        audioFilename: null,
        imageUrls: [], // For slideshow
      };

      // Assign URLs based on uploaded files and type
      if (postType === 'image_text' && uploadedFileData.length > 0) {
          postData.imageUrl = uploadedFileData[0].url;
      } else if (postType === 'video' && uploadedFileData.length > 0) {
           postData.videoUrl = uploadedFileData[0].url;
      } else if (postType === 'image_audio') {
           const imageFile = uploadedFileData.find(f => f.url.includes('image')); // Crude check, improve if needed
           const audioFile = uploadedFileData.find(f => f.url.includes('audio')); // Crude check
           if(imageFile) postData.imageUrl = imageFile.url;
           if(audioFile) {
               postData.audioUrl = audioFile.url;
               postData.audioFilename = audioFile.filename; // Store original filename for download
           }
      } else if (postType === 'slideshow' && uploadedFileData.length > 0) {
          postData.imageUrls = uploadedFileData.map(f => f.url);
      }


      // 3. Create the post via API
      const createdPost = await createPost(postData);

      setSuccessMessage(`Post "${createdPost.title || 'Untitled'}" created successfully!`);
      // Reset form
      setTitle('');
      setTextContent('');
      setFiles([]);
      // Optionally reset postType to default
      // setPostType('text');

    } catch (err) {
      console.error("Submission failed:", err);
      setError(err.response?.data?.message || err.message || 'An error occurred during submission.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Render file previews
  const filePreviews = files.map((file, index) => (
     <div key={index} className="file-preview">
       {file.type.startsWith('image/') ? (
         <img src={URL.createObjectURL(file)} alt={`Preview ${index}`} />
       ) : (
          // Display icon or file name for non-image files
         <div className="file-preview-icon">
            <span>{file.type.startsWith('audio/') ? 'ðŸŽµ' : (file.type.startsWith('video/') ? 'ðŸŽ¬' : 'ðŸ“„')}</span>
            <p>{file.name}</p>
         </div>
       )}
        <button type="button" onClick={() => removeFile(index)} className="remove-file-btn">&times;</button>
     </div>
   ));


  return (
    <div className="submit-post-form-container">
      <h2>Create New Blog Post</h2>

      {/* Post Type Selection */}
      <div className="post-type-selector">
        {['text', 'image_text', 'video', 'image_audio', 'slideshow'].map(type => (
          <button
            key={type}
            type="button"
            className={`type-btn ${postType === type ? 'active' : ''}`}
            onClick={() => handleTypeChange(type)}
          >
            {/* Improve naming conventions */}
            {type.replace('_', ' + ').replace(/\b\w/g, l => l.toUpperCase())}
          </button>
        ))}
      </div>

      <form onSubmit={handleSubmit}>
        {/* Title Input (conditional?) */}
        {postType !== 'text' && ( // Example: Title optional for text posts
            <div className="form-group">
              <label htmlFor="title">Title</label>
              <input
                type="text"
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Post Title"
                disabled={isSubmitting}
              />
            </div>
        )}


        {/* Conditional Fields based on Type */}

        {/* Text Content Area */}
        {(postType === 'text' || postType === 'image_text' || postType === 'image_audio') && (
          <div className="form-group">
            <label htmlFor="textContent">Content</label>
            <ReactQuill
                theme="snow"
                value={textContent}
                onChange={setTextContent}
                modules={quillModules}
                placeholder="Write your content here..."
                readOnly={isSubmitting}
             />
          </div>
        )}

        {/* File Upload / Drag & Drop Area */}
        {(postType === 'image_text' || postType === 'video' || postType === 'image_audio' || postType === 'slideshow') && (
          <div className="form-group">
             <label>
                {postType === 'slideshow' ? 'Images for Slideshow' :
                 (postType === 'image_audio' ? 'Image & Audio Files' :
                 (postType === 'video' ? 'Video File' : 'Image File'))}
             </label>
             <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
                <input {...getInputProps()} />
                {isDragActive ? (
                    <p>Drop the files here ...</p>
                ) : (
                    <p>Drag 'n' drop {postType === 'slideshow' ? 'images' : (postType === 'image_audio' ? 'image/audio' : (postType === 'video' ? 'a video' : 'an image'))} here, or click to select</p>
                )}
             </div>
              {/* File Previews */}
              {files.length > 0 && (
                  <aside className="file-previews-container">
                     <h4>Selected Files:</h4>
                      {filePreviews}
                  </aside>
              )}
          </div>
        )}


        {/* Submit Button & Messages */}
        <div className="form-actions">
          {error && <p className="error-message">{error}</p>}
          {successMessage && <p className="success-message">{successMessage}</p>}
          <button type="submit" disabled={isSubmitting}>
            {isSubmitting ? 'Submitting...' : 'Create Post'}
          </button>
        </div>
      </form>
    </div>
  );
}

export default SubmitPostForm;
