import React, { useState, useRef, useEffect } from "react";
import playlistService from "../services/playlistService";

function Player() {
  const [playlist, setPlaylist] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  // Load playlist from service
  useEffect(() => {
    const tracks = playlistService.getPlaylist();
    setPlaylist(tracks);
  }, []);

  useEffect(() => {
    if (playlist.length === 0) return;

    const audio = audioRef.current;
    if (!audio) return;

    // Fix: Use require() or correct relative path
    const trackUrl = process.env.PUBLIC_URL + playlist[currentTrack].url.replace('public/', '');
    audio.src = trackUrl;
    audio.load();

    if (isPlaying) {
      audio.play().catch((error) => {
        console.error("Playback failed:", error);
      });
    }
  }, [currentTrack, playlist, isPlaying]);

  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.pause();
    } else {
      audio.play().catch((error) => {
        console.error("Error playing:", error);
        // Handle error (e.g., show message to user)
      });
    }
    setIsPlaying(!isPlaying);
  };

  const nextTrack = () => {
    if (playlist.length === 0) return;

    setCurrentTrack((prev) => (prev + 1) % playlist.length);
    setIsPlaying(true);
  };

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const handlePlay = () => setIsPlaying(true);
    const handlePause = () => setIsPlaying(false);
    const handleError = () => {
      console.error("Audio error:", audio.error);
      setIsPlaying(false);
    };

    audio.addEventListener("play", handlePlay);
    audio.addEventListener("pause", handlePause);
    audio.addEventListener("error", handleError);

    return () => {
      audio.removeEventListener("play", handlePlay);
      audio.removeEventListener("pause", handlePause);
      audio.removeEventListener("error", handleError);
    };
  }, []);

  if (playlist.length === 0) {
    return <p>Loading playlist or no tracks available...</p>;
  }

  const currentTrackInfo = playlist[currentTrack];
  const artworkUrl = process.env.PUBLIC_URL + currentTrackInfo.artwork.replace('public/', '');

  return (
    <div
      className="player-container"
      style={{
        backgroundImage: `url(${artworkUrl})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        width: "100%",
        height: "100vh", // Ensure container has size
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "20px",
        boxSizing: "border-box",
      }}
    >
      <audio ref={audioRef} onEnded={nextTrack} />

      <div className="track-info" style={{ 
        backgroundColor: "rgba(0,0,0,0.7)", 
        color: "white", 
        padding: "20px",
        borderRadius: "10px",
        textAlign: "center"
      }}>
        <h2>{currentTrackInfo.title}</h2>
        <p>{currentTrackInfo.artist}</p>
      </div>

      <div className="player-controls" style={{ 
        backgroundColor: "rgba(0,0,0,0.7)", 
        padding: "20px",
        borderRadius: "10px"
      }}>
        <button 
          onClick={togglePlay}
          style={{
            padding: "10px 20px",
            margin: "0 10px",
            fontSize: "16px",
            cursor: "pointer"
          }}
        >
          {isPlaying ? "Pause" : "Play"}
        </button>
        <button 
          onClick={nextTrack}
          style={{
            padding: "10px 20px",
            margin: "0 10px",
            fontSize: "16px",
            cursor: "pointer"
          }}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default Player;
