import React, { useState, useRef, useEffect } from "react";
import playlistService from "../services/playlistService";
import '../styles/player.css'; // Ensure path is correct

// Keep font face if needed for other text, otherwise remove
// const fontFaceStyles = `...`;

function Player() {
    const [playlist, setPlaylist] = useState([]);
    const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const audioRef = useRef(null);
    const originalBodyStyleRef = useRef(null);

    // --- Hooks (Loading, Background, Track Loading, Events) remain the same ---
    useEffect(() => { /* Load Playlist */
        const tracks = playlistService.getPlaylist();
        setPlaylist(tracks); setIsLoadaing(false);
    }, []);

    useEffect(() => { /* Global Background Effect */
        if (playlist.length === 0 || isLoading) return;
        const currentTrackInfo = playlist[currentTrackIndex];
        const artworkUrl = process.env.PUBLIC_URL + currentTrackInfo.artwork.replace('public/', '');
        // Store original styles if not already stored
        if (originalBodyStyleRef.current === null) {
             originalBodyStyleRef.current = { /* ... store relevant body styles ... */ };
        }
        // Apply new styles
        document.body.style.backgroundImage = `url(${artworkUrl})`;
        document.body.style.backgroundSize = 'cover';
        document.body.style.backgroundPosition = 'center center';
        document.body.style.backgroundAttachment = 'fixed';
        document.body.style.minHeight = '100vh';
        document.body.style.transition = 'background-image 1s ease-in-out';
        // Cleanup
        return () => { if (originalBodyStyleRef.current) { /* ... restore body styles ... */ } };
    }, [currentTrackIndex, playlist, isLoading]);

    useEffect(() => { /* Track Loading/Playback */
        if (playlist.length === 0 || isLoading) return;
        const audio = audioRef.current; if (!audio) return;
        const trackUrl = process.env.PUBLIC_URL + playlist[currentTrackIndex].url.replace('public/', '');
        audio.src = trackUrl; audio.load();
        const attemptAutoplay = () => { audio.play().then(/*...*/).catch(/*...*/); };
        const handleEnded = () => { setCurrentTrackIndex(prev => (prev + 1) % playlist.length); };
        audio.addEventListener('ended', handleEnded);
        const canPlayHandler = () => { attemptAutoplay(); audio.removeEventListener('canplay', canPlayHandler); }
        audio.addEventListener('canplay', canPlayHandler);
        return () => { /* remove listeners */ };
    }, [currentTrackIndex, playlist, isLoading]);

    useEffect(() => { /* Audio State Events */
        const audio = audioRef.current; if (!audio) return;
        const handlePlay = () => setIsPlaying(true); const handlePause = () => setIsPlaying(false); const handleError = (e) => console.error("Audio error:", e);
        audio.addEventListener('play', handlePlay); audio.addEventListener('pause', handlePause); audio.addEventListener('error', handleError);
        return () => { /* remove listeners */ };
    }, [playlist.length]);

    // --- Control Functions ---
    const togglePlay = () => {
        const audio = audioRef.current; if (!audio) return;
        if (isPlaying) { audio.pause(); }
        else { audio.play().catch(error => { console.error("Playback error:", error); setIsPlaying(false); }); }
    };

    const rewind15Seconds = () => {
        const audio = audioRef.current;
        if (audio) {
            audio.currentTime = Math.max(0, audio.currentTime - 15); // Go back 15s, but not before 0
        }
    };

    // --- Loading State ---
    if (isLoading || playlist.length === 0) {
        return <div className="loading-container alt-loading"><p>Loading Station...</p></div>;
    }

    // --- Render Centered Player UI ---
    const currentTrackInfo = playlist[currentTrackIndex];
    const artworkUrl = process.env.PUBLIC_URL + currentTrackInfo.artwork.replace('public/', ''); // Need URL here too for artwork div

    return (
        <>
            {/* Inject font styles if needed globally */}
            {/* <style dangerouslySetInnerHTML={{ __html: fontFaceStyles }} /> */}

            <audio ref={audioRef} preload="metadata" />

            {/* Main container for the centered UI */}
            <div className="player-ui-container">

                {/* Artwork Display within the UI */}
                <div
                    className="player-artwork"
                    style={{ backgroundImage: `url(${artworkUrl})` }} // Set artwork here
                ></div>

                {/* Track Details */}
                <div className="player-track-details">
                    <h2>{currentTrackInfo.title}</h2>
                    {currentTrackInfo.artist && <p>{currentTrackInfo.artist}</p>}
                </div>

                {/* Player Controls */}
                <div className="player-controls-centered">
                    <button onClick={rewind15Seconds} className="control-button rewind-button" aria-label="Rewind 15 seconds">
                        {/* Use an icon font/SVG here ideally, text fallback: */}
                        ↺15
                    </button>
                    <button onClick={togglePlay} className="control-button play-pause-main" aria-label={isPlaying ? "Pause" : "Play"}>
                        {/* Use icon font/SVG ideally */}
                        {isPlaying ? '❙❙' : '▶'}
                         {/* Using slightly different icons */}
                    </button>
                    {/* Add a placeholder for visual balance if needed, or a forward skip? */}
                    <div className="control-button-placeholder"></div>
                </div>

            </div>
        </>
    );
}

export default Player;
