// src/App.js (Simplified Example)
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Import Navigate
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Player from './components/Player';
import Blog from './pages/Blog';
// Removed Submit page import, maybe rename or use new component
// import Submit from './pages/Submit';
import Help from './pages/Help';
import Login from './components/AuthSystem/Login';
import Register from './components/AuthSystem/Register';
import SubmitPostForm from './components/SubmitPostForm'; // Import the new form
import './App.css';

// --- Authentication Logic Placeholder ---
// In a real app, this would likely come from context or a zustand/redux store
const checkAdminStatus = (user) => {
    // Replace with your actual admin check logic
    return user && user.role === 'admin';
};
// --- End Placeholder ---

function AppContent() {
  const location = useLocation();
  const isHomePage = location.pathname === '/';
  const bottomPlayerBarHeight = '80px';

  // Assuming user state is managed here or via context
  const [user, setUser] = useState(null); // Get user state from login/context
  const isAdmin = checkAdminStatus(user); // Check if user is admin

  return (
    <div className="app-container">
      <Navbar user={user} setUser={setUser} />

      <div
        className="main-content-area"
        style={{ paddingBottom: !isHomePage ? bottomPlayerBarHeight : '0' }}
      >
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/blog" element={<Blog />} />
          {/* Help route accessible to all */}
          <Route path="/help" element={<Help />} />

          {/* Auth Routes */}
          <Route path="/login" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register setUser={setUser} />} />

          {/* Protected Admin Submit Route */}
          <Route
            path="/submit" // Or use '/admin/submit'
            element={
              isAdmin ? (
                <SubmitPostForm />
              ) : (
                // Redirect non-admins away (e.g., to login or homepage)
                <Navigate to="/login" replace state={{ from: location }} />
                // Or show an "Access Denied" component
                // <AccessDenied />
              )
            }
          />

          {/* Catch-all or 404 route */}
          {/* <Route path="*" element={<NotFound />} /> */}
        </Routes>
      </div>
      <Player />
    </div>
  );
}

function App() {
   // Assuming Router is setup here based on previous context
   return <AppContent />;
}

export default App;
