import React, { useState, useRef, useEffect } from "react";
src/services/playlistService.js
function Player() {
  const [playlist, setPlaylist] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  // Load playlist from service
  useEffect(() => {
    setPlaylist(playlistService.getPlaylist());
  }, []);

  useEffect(() => {
    if (isPlaying && audioRef.current) {
      audioRef.current.play();
    } else if (audioRef.current) {
      audioRef.current.pause();
    }
  }, [currentTrack, isPlaying]);

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  const nextTrack = () => {
    setCurrentTrack((prev) => (prev + 1) % playlist.length);
  };

  if (playlist.length === 0) {
    return <p>Loading playlist...</p>;
  }

  const currentTrackInfo = playlist[currentTrack];

  return (
    <div 
      className="player-container" 
      style={{ 
        backgroundImage: `url(${currentTrackInfo.artwork})`,
        backgroundSize: "cover",
        backgroundPosition: "center"
      }}
    >
      <audio 
        ref={audioRef} 
        src={currentTrackInfo.url} 
        onEnded={nextTrack}
      />

      <div className="track-info">
        <h2>{currentTrackInfo.title}</h2>
        <p>{currentTrackInfo.artist}</p>
      </div>

      <div className="player-controls">
        <button onClick={togglePlay}>
          {isPlaying ? "Pause" : "Play"}
        </button>
        <button onClick={nextTrack}>Next</button>
      </div>
    </div>
  );
}

export default Player;
